 9        int gap = 1;
10        while (gap &lt; arr.length) {
11            gap = gap * 3 + 1;
12        }
13
14        while (gap &gt; 0) {
15            for (int i = gap; i &lt; arr.length; i++) {
16                int tmp = arr[i];
17                int j = i - gap;
18                while (j &gt;= 0 &amp;&amp; arr[j] &gt; tmp) {
19                    arr[j + gap] = arr[j];
20                    j -= gap;
21                }
22                arr[j + gap] = tmp;
23            }
24            gap = (int) Math.floor(gap / 3);
25        }
26
27        return arr;
28    }
