public String removeDuplicateLetters(String s) {
    // ä¿å­˜ç»“æœï¼Œæ ˆä½æ˜¯æœ€å°å€¼
    Stack&lt;Character&gt; stack = new Stack&lt;&gt;();
    // æ˜¯å¦å·²ç»æœ‰åœ¨æ ˆé‡Œï¼Œå¦‚æœå·²ç»å¤„ç†ï¼Œåˆ™ä¸å¤„ç†ã€‚
    HashSet&lt;Character&gt; seen = new HashSet&lt;&gt;();
    // ç”¨æ¥åˆ¤æ–­ï¼Œå½“å‰æœ€åå‡ºç°çš„ä½ç½®ã€‚å¦‚æœåç»­æ²¡æœ‰é‡å¤çš„å€¼äº†(å³æ˜¯æœ€åä¸€ä¸ªäº†)ï¼Œåˆ™ä¸èƒ½å‡ºæ ˆã€‚
    HashMap&lt;Character, Integer&gt; last_occur = new HashMap&lt;&gt;();
    for (int i = 0; i &lt; s.length(); i++){
        last_occur.put(s.charAt(i), i); 
    }
    // éå†å­—ç¬¦ä¸²ï¼Œåˆ¤æ–­æ˜¯å¦å…¥æ ˆ
    for (int i = 0; i &lt; s.length(); i++){
        char c = s.charAt(i);
        if (!seen.contains(c)){
            while (!stack.isEmpty() &amp;&amp;
                    c &lt; stack.peek() &amp;&amp;
                    last_occur.get(stack.peek()) &gt; i){
                seen.remove(stack.pop());
            }
            seen.add(c);
            stack.push(c);
        }
    }
    // å°†æ ˆä¸­å…ƒç´ ä¿å­˜æˆå­—ç¬¦ä¸²è¾“å‡º
    StringBuilder sb = new StringBuilder(stack.size());
    for (Character c: stack){
        sb.append(c.charValue());
    }
    return sb.toString();
}


