    result[0] = maxHeap.peek();
    // éå†æ•°ç»„
    for(int i = 1; i &lt;= nums.length - k; i++){
        maxHeap.remove(nums[i - 1]); 
        maxHeap.add(nums[i + k - 1]); 
        result[i] = maxHeap.peek();
    }
    return result;
}


