class Solution { 
    public int[][] findContinuousSequence(int target) {
        List&lt;int[]&gt; res = new ArrayList&lt;&gt;(); 
        int left = 1;  
        int right = 1;  
        int win = 0;  
        // ä»¥ä¸­æ•°è¿›è¡Œåˆ¤æ–­ã€‚å·¦åŸºå‡†
        while (left &lt;= target / 2) {
            if (win &lt; target) {
                win += right;
                right++;
            } else if (win &gt; target) {
                win -= left;
                left++;
            } else {
                int[] arr = new int[right-left];
                for (int k = left; k &lt; right; k++) {
                    arr[k-left] = k;
                }
                res.add(arr);
                win -= left;
                left++;
            }
        }
        return res.toArray(new int[res.size()][]);
    }
}
